def three_sum(nums, target):
    nums_dict = {}
    for i, num in enumerate(nums):
        nums_dict[num] = i
    for i in range(len(nums)):
        for j in range(i + 1, len(nums)):
            complement = target - nums[i] - nums[j]
            if complement in nums_dict and nums_dict[complement] != i and nums_dict[complement] != j:
                return [i, j, nums_dict[complement]]

