      "code": "import argparse
import os
import shutil


def organize_files(directory_path):
    file_types = {
        'images': ['.jpg', '.jpeg', '.png', '.gif'],
        'documents': ['.pdf', '.doc', '.docx', '.txt'],
        'audio': ['.mp3', '.wav', '.m4a']
    }

    for folder, extensions in file_types.items():
        folder_path = os.path.join(directory_path, folder)
        if not os.path.exists(folder_path):
            os.makedirs(folder_path)

        for ext in extensions:
            for file in os.listdir(directory_path):
                if file.endswith(ext):
                    shutil.move(os.path.join(directory_path, file), folder_path)


if __name__ == '__main__':
    parser = argparse.ArgumentParser(description='Organize files in a directory based on their file types.')
    parser.add_argument('--directory_path', type=str, required=True, help='The path to the directory to organize.')

    args = parser.parse_args()
    organize_files(args.directory_path)",
      "name": "organize_files.py"
    }
  }
}

